const TELEGRAM_CHAR_LIMIT = 4000;
const longText = $input.first().json.text
const paragraphs = longText.split('\n\n');

const messageChunks = [];
let currentChunk = '';

for (const paragraph of paragraphs) {
  if ((currentChunk.length + paragraph.length + 2) > TELEGRAM_CHAR_LIMIT) {
    messageChunks.push(currentChunk);
    currentChunk = paragraph;
  } else {

    if (currentChunk.length > 0) {
      currentChunk += '\n\n' + paragraph;
    } else {
      currentChunk = paragraph;
    }
  }
}
if (currentChunk) {
  messageChunks.push(currentChunk);
}

return messageChunks.map(chunk => {
  return {
    json: {
      textChunk: chunk
    }
  };
});

// This code will process each item from the input.
const resultItems = [];

for (const item of items) {
  // Get the value from the 'output' field.
  const outputValue = item.json.output;

  try {
    // First, try to parse the output value as a JSON string.
    // This will succeed for your first input type.
    const parsedJson = JSON.parse(outputValue);

    // If successful, the result is the parsed JSON object.
    resultItems.push({ json: parsedJson });

  } catch (error) {
    // If JSON.parse() fails, it's because the input is plain text.
    // In that case, we create a new object and put the text inside it.
    resultItems.push({
      json: {
        text: outputValue
      }
    });
  }
}

// Return the structured JSON items.
return resultItems;
