return items.filter(item => {
  let callIds = item.json.call_id;
  let entryPoints = item.json.entry_point_call_id;

  // Ensure both are arrays
  callIds = Array.isArray(callIds) ? callIds : [callIds];
  entryPoints = Array.isArray(entryPoints) ? entryPoints : [entryPoints];

  // Filter out null/undefined values
  callIds = callIds.filter(id => id != null);
  entryPoints = entryPoints.filter(id => id != null);

  // Update the item with cleaned arrays
  item.json.call_id = callIds;
  item.json.entry_point_call_id = entryPoints;

  // Keep only if there's at least one valid entry_point_call_id
  return entryPoints.length > 0;
});

const input = $input.first().json
const entryPointCallIds = input.entry_point_call_id;
// Create the MongoDB query object
const mongoQuery = {
  call_id: { "$in": entryPointCallIds }
};

// Convert to JSON string for n8n MongoDB node
return {
  json: {
    mongoQuery: JSON.stringify(mongoQuery),
    contact_name:input.name,
    contact_id: input.contact_id
  }
};

return items.map((item, index) => {
  // Access previous input item (before MongoDB)
  const prev = $items('Code1', 0)[index];  // Replace 'NodeBeforeMongoDB' with actual node name$('Code1').first().json.mongoQueryString

  if (!prev) return item; // just in case

  // Inject contact info
  item.json.contact_id = prev.json.contact_id;
  item.json.name = prev.json.name;

  return item;
});

// Get the input item
const inputItem = $input.first().json;

// Extract contact info with fallbacks
const contactId = inputItem.contact_id || inputItem.id;
const contactName = inputItem.contact_name || inputItem.name;

// Handle data array (could be directly in inputItem or nested)
let dataArray = inputItem.data || [];

// If data is not an array, try to make it one
if (!Array.isArray(dataArray)) {
  dataArray = [dataArray];
}

// Build the output object
const outputObject = {
  contact_id: contactId,
  contact_name: contactName
};

// Process each data entry dynamically
dataArray.forEach((dataEntry, index) => {
  if (!dataEntry) return; // Skip null/undefined entries

  const dataKey = `data_${index}`;

  outputObject[dataKey] = {
    agent_name: dataEntry.agent_name || 
                dataEntry.agen_name || 
                dataEntry.agentName || 
                'Unknown',
    [`transcript_${index}`]: dataEntry.transcript || 
                            dataEntry.transcript_text || 
                            dataEntry.transcriptText || 
                            dataEntry.content || 
                            ''
  };
});

return [{
    data: JSON.stringify(outputObject)
}];

// Get all items from the aggregation
const allItems = items;
const groupedByContact = {};

// Process each aggregated item
allItems.forEach(item => {
  const dataArray = item.json.data || [];

  // Handle each data entry (could be single or multiple per item)
  dataArray.forEach(dataEntry => {
    const contactId = dataEntry.contact_id;
    const contactName = dataEntry.contact_name;

    if (!contactId) return; // Skip if no contact_id

    // Initialize group if it doesn't exist
    if (!groupedByContact[contactId]) {
      groupedByContact[contactId] = {
        contact_id: contactId,
        contact_name: contactName,
        name: dataEntry.name, // Assuming name is consistent for same contact
        data: []
      };
    }

    // Add this data entry to the contact's group
    groupedByContact[contactId].data.push(dataEntry);
  });
});

// Convert grouped object back to array format for n8n
return Object.values(groupedByContact).map(group => ({
  json: group
}));
