// "items" is an array of 3000 items from the Merge node.
// We'll collect them all in a single array called "allCandles".
const allCandles = [];

for (const item of items) {
  allCandles.push(item.json);
}

// Return a single item with a property named "allCandles" (an array of 3000).
return [{
  json: {
    allCandles
  }
}];

const articles = $json.articles || [];
const filteredArticles = articles.map(article => ({
  title: article.title,
  description: article.description,
}));

return [{
  json: {
    filteredArticles
  }
}];

// Initialize containers for each set of data.
const allCandles = [];
let contentData = null;

// Loop over each item from the merge node.
for (const item of items) {
  // If the item has candlestick data, add it to the array.
  if (item.json.allCandles !== undefined) {
    // Assuming item.json.allCandles is an array.
    allCandles.push(...item.json.allCandles);
  }
  // If the item has embedded content (in message.content), store it.
  if (item.json.message && item.json.message.content !== undefined) {
    contentData = item.json.message.content;
  }
}

// Return a single item with both candlestick data and content.
return [{
  json: {
    allCandles,
    content: contentData
  }
}];

// Retrieve the user input from the first input item's message text.
const coinInput = $input.first().json.message.text || "";

// Clean the input by trimming whitespace and convert it to uppercase.
const ticker = coinInput.trim().toUpperCase();

// Append "USDT" to the ticker to form the complete trading pair symbol.
const symbol = ticker + "USDT";

// Output the result as a JSON object.
return [
  { json: { symbol } }
];

const inputText = $json["output"] || "";
if (typeof inputText !== "string") {
  throw new Error("Input must be a string");
}
const mid = Math.ceil(inputText.length / 2);
const firstHalf = inputText.substring(0, mid);
const secondHalf = inputText.substring(mid);
return [
  { json: { blockNumber: 1, content: firstHalf } },
  { json: { blockNumber: 2, content: secondHalf } },
];

