const inputText = $json["output"] || "";
if (typeof inputText !== "string") {
  throw new Error("Input must be a string");
}
const mid = Math.ceil(inputText.length / 2);
const firstHalf = inputText.substring(0, mid);
const secondHalf = inputText.substring(mid);
return [
  { json: { blockNumber: 1, content: firstHalf } },
  { json: { blockNumber: 2, content: secondHalf } },
];

const videos = $node["Fetch Pexels Video"].json.videos;
const randomIndex = Math.floor(Math.random() * videos.length);
const videoUrl = videos[randomIndex].video_files[0].link;
return [{ json: { videoUrl } }];

const crypto = require('crypto');
const secret = 'YOUR_TRANSLOADIT_SECRET';
const params = {
  auth: { key: 'YOUR_TRANSLOADIT_API_KEY' },
  template_id: 'aad3cdcd6d804111be68cb7d4414385d'
};
const paramsString = JSON.stringify(params);
const signature = crypto.createHmac('sha1', secret).update(paramsString).digest('hex');
return [{ json: { params: paramsString, signature } }];
